Scripts.OnEnter:
                if ( self.Label:IsTruncated() ) then
				GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
				GameTooltip:SetText(self.Label:GetText());
				GameTooltip:Show();
                end

GameTooltip:SetOwner(self:GetParent(), "ANCHOR_BOTTOMRIGHT");
GameTooltip:SetText(T("Boss status colors"), nil, nil, nil, nil, true);
GameTooltip:AddLine("|cffb3b3b3"..T("Any")..", |cff00ff00"..T("Alive")..", |cffff0000"..T("Defeated"));
GameTooltip:Show();

Scripts.OnUpdate:
if PremadeFilter_Frame.ShowNextTime and not PremadeFilter_Frame:IsVisible() then
PremadeFilter_Frame:Show();
PremadeFilter_Frame.ShowNextTime = false;
end

Scripts.OnEnterPressed:
if PremadeFilter_Frame.AutoCompleteFrame:IsVisible() then
local idx = PremadeFilter_Frame.AutoCompleteFrame.selectedIndex
if idx then
local button = PremadeFilter_Frame.AutoCompleteFrame.Results[idx];
PremadeFilter_AutoCompleteButton_OnClick(button);
end
else
LFGListSearchPanel_DoSearch(self:GetParent():GetParent());
self:ClearFocus();
end

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
PremadeFilter_AutoCompleteAdvance(-1);
else
PremadeFilter_AutoCompleteAdvance(1);
end

Scripts.OnArrowPressed:
if ( key == "UP" ) then
PremadeFilter_AutoCompleteAdvance(-1);
elseif ( key == "DOWN" ) then
PremadeFilter_AutoCompleteAdvance(1);
end

Scripts.OnLoad:
InputScrollFrame_OnLoad(self);
self.EditBox.tabCategory = self.tabCategory;
LFGListEditBox_AddToTabCategory(self.EditBox, self.tabCategory);
self.EditBox:SetScript("OnTabPressed", LFGListEditBox_OnTabPressed);

Scripts.OnClick:
if self:GetChecked() then
self:GetParent().PlayersNoTankMinLabel:Hide();
self:GetParent().PlayersNoTankMaxLabel:Hide();
self:GetParent().MinTanks:Show();
self:GetParent().MaxTanks:Show();
self:GetParent().MinTanks:SetFocus();
else
self:GetParent().PlayersNoTankMinLabel:Show();
self:GetParent().PlayersNoTankMaxLabel:Show();
self:GetParent().MinTanks:Hide();
self:GetParent().MaxTanks:Hide();
self:GetParent().MinTanks:SetText("");
self:GetParent().MaxTanks:SetText("");
end
PremadeFilter_CheckButtonSound(self);

if self:GetChecked() then
self:GetParent().PlayersNoHealerMinLabel:Hide();
self:GetParent().PlayersNoHealerMaxLabel:Hide();
self:GetParent().MinHealers:Show();
self:GetParent().MaxHealers:Show();
self:GetParent().MinHealers:SetFocus();
else
self:GetParent().PlayersNoHealerMinLabel:Show();
self:GetParent().PlayersNoHealerMaxLabel:Show();
self:GetParent().MinHealers:Hide();
self:GetParent().MaxHealers:Hide();
self:GetParent().MinHealers:SetText("");
self:GetParent().MaxHealers:SetText("");
end
PremadeFilter_CheckButtonSound(self);

if self:GetChecked() then
self:GetParent().PlayersNoDamagerMinLabel:Hide();
self:GetParent().PlayersNoDamagerMaxLabel:Hide();
self:GetParent().MinDamagers:Show();
self:GetParent().MaxDamagers:Show();
self:GetParent().MinDamagers:SetFocus();
else
self:GetParent().PlayersNoDamagerMinLabel:Show();
self:GetParent().PlayersNoDamagerMaxLabel:Show();
self:GetParent().MinDamagers:Hide();
self:GetParent().MaxDamagers:Hide();
self:GetParent().MinDamagers:SetText("");
self:GetParent().MaxDamagers:SetText("");
end
PremadeFilter_CheckButtonSound(self);

Scripts.OnLoad:
self.IncludeLabel:SetText(T("Include words"));
self.ExcludeLabel:SetText(T("Exclude words"));
self.PossibleLabel:SetText(T("At least one word"));

Scripts.OnShow:
-- setup option values
self.NewGroupChatNotifications:SetChecked(PremadeFilter_GetSettings("NewGroupChatNotifications", true));
self.NewPlayerChatNotifications:SetChecked(PremadeFilter_GetSettings("NewPlayerChatNotifications", true));
self.SoundNotifications:SetChecked(PremadeFilter_GetSettings("SoundNotifications", true));
self.UpdateInterval:SetValue(PremadeFilter_GetSettings("UpdateInterval", 15));
PremadeFilter_UpdateIntervalShortAlert:SetText(T("Attention! Update interval shorter than 15 seconds may cause the game to run slow and impact your gaming experience!!!"));
PremadeFilter_UpdateIntervalShortAlert_UpdateVisibility(PremadeFilter_UpdateIntervalShortAlert);
